/* Custom global styles */
body {
    -webkit-font-smoothing: antialiased;
    -moz-osx-font-smoothing: grayscale;
}

.font-serif {
    font-family: 'Playfair Display', serif;
}

.shadow-text {
    text-shadow: 0 2px 4px rgba(0,0,0,0.5);
}

/* Custom animation for card appearance */
.card-enter {
    opacity: 0;
    transform: translateY(20px);
    transition: opacity 0.5s ease, transform 0.5s ease;
}

.card-enter-active {
    opacity: 1;
    transform: translateY(0);
}

/* Tab styling */
.tab-button.active {
    @apply border-brown-700 text-brown-700;
}

document.addEventListener('DOMContentLoaded', () => {
    const featuredShopsContainer = document.getElementById('featured-shops');
    const searchForm = document.getElementById('search-form');
    const searchInput = document.getElementById('search-input');

    const renderStarRating = (rating) => {
        let stars = '';
        const fullStars = Math.floor(rating);
        const halfStar = rating % 1 !== 0;
        for (let i = 0; i < fullStars; i++) {
            stars += '<i class="fas fa-star text-yellow-400"></i>';
        }
        if (halfStar) {
            stars += '<i class="fas fa-star-half-alt text-yellow-400"></i>';
        }
        for (let i = 0; i < 5 - Math.ceil(rating); i++) {
            stars += '<i class="far fa-star text-yellow-400"></i>';
        }
        return stars;
    };

    const createShopCard = (shop) => {
        const card = document.createElement('div');
        card.className = 'bg-cream-100 rounded-lg shadow-lg overflow-hidden transform hover:-translate-y-2 transition-transform duration-300 card-enter';
        card.innerHTML = `
            <img src="${shop.imageUrl}" alt="${shop.name}" class="w-full h-48 object-cover">
            <div class="p-6">
                <h3 class="text-xl font-bold mb-2 font-serif">${shop.name}</h3>
                <p class="text-brown-700 mb-2"><i class="fas fa-map-marker-alt mr-2"></i>${shop.address}</p>
                <div class="flex items-center mb-4">
                    <div class="flex mr-2">${renderStarRating(shop.rating)}</div>
                    <span class="text-sm text-brown-500">(${shop.rating})</span>
                </div>
                <a href="/shop-detail.html?id=${shop.id}" class="inline-block bg-brown-700 text-white px-6 py-2 rounded-full hover:bg-brown-900 transition">View Details</a>
            </div>
        `;
        return card;
    };

    if (featuredShopsContainer) {
        const featuredShops = coffeeShops.filter(shop => shop.featured);
        featuredShops.forEach(shop => {
            const card = createShopCard(shop);
            featuredShopsContainer.appendChild(card);
        });
        // Animate cards on load
        setTimeout(() => {
            document.querySelectorAll('.card-enter').forEach(el => el.classList.add('card-enter-active'));
        }, 100);
    }

    if (searchForm) {
        searchForm.addEventListener('submit', (e) => {
            e.preventDefault();
            const query = searchInput.value;
            window.location.href = `/shops.html?search=${encodeURIComponent(query)}`;
        });
    }
});

document.addEventListener('DOMContentLoaded', () => {
    const shopsListContainer = document.getElementById('shops-list');
    const noResultsDiv = document.getElementById('no-results');
    const searchInput = document.getElementById('search-shops-input');
    const ratingFilter = document.getElementById('filter-rating');
    const clearButton = document.getElementById('clear-filters');

    const renderStarRating = (rating) => {
        let stars = '';
        const fullStars = Math.floor(rating);
        const halfStar = rating % 1 !== 0;
        for (let i = 0; i < fullStars; i++) {
            stars += '<i class="fas fa-star text-yellow-400"></i>';
        }
        if (halfStar) {
            stars += '<i class="fas fa-star-half-alt text-yellow-400"></i>';
        }
        for (let i = 0; i < 5 - Math.ceil(rating); i++) {
            stars += '<i class="far fa-star text-yellow-400"></i>';
        }
        return stars;
    };

    const createShopCard = (shop) => {
        const card = document.createElement('div');
        card.className = 'bg-cream-100 rounded-lg shadow-lg overflow-hidden transform hover:-translate-y-2 transition-transform duration-300 card-enter';
        card.innerHTML = `
            <img src="${shop.imageUrl}" alt="${shop.name}" class="w-full h-48 object-cover">
            <div class="p-6">
                <h3 class="text-xl font-bold mb-2 font-serif">${shop.name}</h3>
                <p class="text-brown-700 mb-2"><i class="fas fa-map-marker-alt mr-2"></i>${shop.address}</p>
                <div class="flex items-center mb-4">
                    <div class="flex mr-2">${renderStarRating(shop.rating)}</div>
                    <span class="text-sm text-brown-500">(${shop.rating})</span>
                </div>
                <a href="/shop-detail.html?id=${shop.id}" class="inline-block bg-brown-700 text-white px-6 py-2 rounded-full hover:bg-brown-900 transition">View Menu & Reviews</a>
            </div>
        `;
        return card;
    };

    const displayShops = (shops) => {
        shopsListContainer.innerHTML = '';
        if (shops.length === 0) {
            noResultsDiv.classList.remove('hidden');
        } else {
            noResultsDiv.classList.add('hidden');
            shops.forEach(shop => {
                const card = createShopCard(shop);
                shopsListContainer.appendChild(card);
            });
            // Animate cards on display
            setTimeout(() => {
                document.querySelectorAll('.card-enter').forEach(el => el.classList.add('card-enter-active'));
            }, 100);
        }
    };

    const filterShops = () => {
        const searchTerm = searchInput.value.toLowerCase();
        const minRating = parseFloat(ratingFilter.value);

        const filteredShops = coffeeShops.filter(shop => {
            const nameMatch = shop.name.toLowerCase().includes(searchTerm);
            const ratingMatch = shop.rating >= minRating;
            return nameMatch && ratingMatch;
        });

        displayShops(filteredShops);
    };

    // Initial display and handling of search query from URL
    const urlParams = new URLSearchParams(window.location.search);
    const searchQuery = urlParams.get('search');
    if (searchQuery) {
        searchInput.value = searchQuery;
    }

    filterShops();

    searchInput.addEventListener('input', filterShops);
    ratingFilter.addEventListener('change', filterShops);
    clearButton.addEventListener('click', () => {
        searchInput.value = '';
        ratingFilter.value = '0';
        filterShops();
    });
});

document.addEventListener('DOMContentLoaded', () => {
    const container = document.getElementById('shop-detail-container');
    const urlParams = new URLSearchParams(window.location.search);
    const shopId = parseInt(urlParams.get('id'));
    const shop = coffeeShops.find(s => s.id === shopId);

    if (!shop) {
        container.innerHTML = `<p class="text-center text-xl">Shop not found.</p>`;
        return;
    }

    const renderStarRating = (rating) => {
        let stars = '';
        const fullStars = Math.floor(rating);
        const halfStar = rating % 1 !== 0;
        for (let i = 0; i < fullStars; i++) stars += '<i class="fas fa-star text-yellow-400"></i>';
        if (halfStar) stars += '<i class="fas fa-star-half-alt text-yellow-400"></i>';
        for (let i = 0; i < 5 - Math.ceil(rating); i++) stars += '<i class="far fa-star text-yellow-400"></i>';
        return stars;
    };

    const renderShopDetails = () => {
        container.innerHTML = `
            <div class="bg-white rounded-xl shadow-2xl overflow-hidden">
                <div class="md:flex">
                    <div class="md:w-1/2">
                        <img src="${shop.imageUrl}" alt="${shop.name}" class="h-64 w-full object-cover md:h-full">
                    </div>
                    <div class="p-8 md:w-1/2">
                        <h1 class="text-4xl font-bold font-serif mb-2">${shop.name}</h1>
                        <p class="text-brown-700 mb-4"><i class="fas fa-map-marker-alt mr-2"></i>${shop.address}</p>
                        <div class="flex items-center mb-4">
                            <div class="flex mr-2">${renderStarRating(shop.rating)}</div>
                            <span class="text-sm text-brown-500">(${shop.rating} from ${shop.reviews.length} reviews)</span>
                        </div>
                        <p class="text-brown-700"><i class="fas fa-clock mr-2"></i>Open: ${shop.hours}</p>
                    </div>
                </div>
            </div>

            <div class="mt-12">
                <div class="border-b border-cream-300">
                    <nav class="-mb-px flex space-x-8" aria-label="Tabs">
                        <button id="tab-menu" class="tab-button whitespace-nowrap py-4 px-1 border-b-2 font-medium text-lg border-transparent text-gray-500 hover:text-gray-700 hover:border-gray-300 active">Menu</button>
                        <button id="tab-reviews" class="tab-button whitespace-nowrap py-4 px-1 border-b-2 font-medium text-lg border-transparent text-gray-500 hover:text-gray-700 hover:border-gray-300">Reviews</button>
                    </nav>
                </div>
                <div id="tab-content" class="mt-8"></div>
            </div>
        `;
        renderMenu();
        setupTabs();
    };

    const renderMenu = () => {
        const content = document.getElementById('tab-content');
        let menuHtml = '<div class="grid md:grid-cols-2 gap-6">';
        shop.menu.forEach(item => {
            menuHtml += `
                <div class="bg-white p-4 rounded-lg shadow-md flex justify-between items-center">
                    <div>
                        <h4 class="font-semibold">${item.name}</h4>
                        <p class="text-sm text-brown-500">$${item.price.toFixed(2)}</p>
                    </div>
                    <button data-item-id="${item.id}" data-shop-id="${shop.id}" class="add-to-cart-btn bg-brown-700 text-white px-4 py-2 rounded-full hover:bg-brown-900 text-sm">Add</button>
                </div>
            `;
        });
        menuHtml += '</div>';
        content.innerHTML = menuHtml;
        addCartButtonListeners();
    };

    const renderReviews = () => {
        const content = document.getElementById('tab-content');
        let reviewsHtml = '<div class="space-y-6">';
        shop.reviews.forEach(review => {
            reviewsHtml += `
                <div class="bg-white p-6 rounded-lg shadow-md">
                    <div class="flex items-center mb-2">
                        <p class="font-semibold mr-4">${review.user}</p>
                        <div class="flex">${renderStarRating(review.rating)}</div>
                    </div>
                    <p class="text-brown-700 italic">"${review.comment}"</p>
                </div>
            `;
        });
        reviewsHtml += '</div>';
        content.innerHTML = reviewsHtml;
    };

    const setupTabs = () => {
        const menuTab = document.getElementById('tab-menu');
        const reviewsTab = document.getElementById('tab-reviews');
        menuTab.addEventListener('click', () => {
            menuTab.classList.add('active');
            reviewsTab.classList.remove('active');
            renderMenu();
        });
        reviewsTab.addEventListener('click', () => {
            reviewsTab.classList.add('active');
            menuTab.classList.remove('active');
            renderReviews();
        });
    };

    const addCartButtonListeners = () => {
        document.querySelectorAll('.add-to-cart-btn').forEach(button => {
            button.addEventListener('click', (e) => {
                const itemId = e.target.dataset.itemId;
                const shopId = parseInt(e.target.dataset.shopId);
                addToCart(shopId, itemId);
                e.target.textContent = 'Added!';
                e.target.classList.add('bg-green-500');
                setTimeout(() => {
                   e.target.textContent = 'Add';
                   e.target.classList.remove('bg-green-500');
                }, 1000);
            });
        });
    };

    const addToCart = (shopId, itemId) => {
        let cart = JSON.parse(localStorage.getItem('cart')) || { shopId: null, items: {} };

        if (cart.shopId && cart.shopId !== shopId) {
            if (!confirm('You have items from another shop. Clear cart and add this item?')) {
                return;
            }
            cart = { shopId: shopId, items: {} };
        }

        cart.shopId = shopId;
        const itemKey = itemId;
        if (cart.items[itemKey]) {
            cart.items[itemKey].quantity++;
        } else {
            const shop = coffeeShops.find(s => s.id === shopId);
            const menuItem = shop.menu.find(m => m.id === itemId);
            cart.items[itemKey] = { ...menuItem, quantity: 1 };
        }

        localStorage.setItem('cart', JSON.stringify(cart));
    };

    renderShopDetails();
});

document.addEventListener('DOMContentLoaded', () => {
    const orderContainer = document.getElementById('order-container');
    let cart = JSON.parse(localStorage.getItem('cart')) || { items: {} };

    const renderCart = () => {
        if (!cart.shopId || Object.keys(cart.items).length === 0) {
            orderContainer.innerHTML = `
                <div class="text-center bg-white p-8 rounded-lg shadow-md">
                    <i class="fas fa-shopping-cart text-4xl text-brown-500 mb-4"></i>
                    <h2 class="text-2xl font-semibold mb-2">Your Cart is Empty</h2>
                    <p class="text-brown-700 mb-6">Looks like you haven't added anything to your order yet.</p>
                    <a href="/shops.html" class="bg-brown-700 text-white px-6 py-3 rounded-full hover:bg-brown-900 transition">Start Shopping</a>
                </div>
            `;
            return;
        }

        const shop = coffeeShops.find(s => s.id === cart.shopId);
        let itemsHtml = '';
        let subtotal = 0;

        Object.values(cart.items).forEach(item => {
            const itemTotal = item.price * item.quantity;
            subtotal += itemTotal;
            itemsHtml += `
                <div class="flex justify-between items-center py-4 border-b border-cream-300">
                    <div>
                        <p class="font-semibold">${item.name}</p>
                        <p class="text-sm text-brown-500">$${item.price.toFixed(2)}</p>
                    </div>
                    <div class="flex items-center gap-4">
                        <div class="flex items-center border border-cream-300 rounded-full">
                            <button data-item-id="${item.id}" class="quantity-change-btn w-8 h-8 text-lg">-</button>
                            <span class="w-8 text-center">${item.quantity}</span>
                            <button data-item-id="${item.id}" class="quantity-change-btn w-8 h-8 text-lg">+</button>
                        </div>
                        <p class="font-semibold w-16 text-right">$${itemTotal.toFixed(2)}</p>
                        <button data-item-id="${item.id}" class="remove-item-btn text-red-500 hover:text-red-700"><i class="fas fa-trash"></i></button>
                    </div>
                </div>
            `;
        });

        orderContainer.innerHTML = `
            <div class="bg-white p-8 rounded-xl shadow-xl">
                <h2 class="text-2xl font-bold mb-4 font-serif">Order from ${shop.name}</h2>
                <div id="cart-items">${itemsHtml}</div>
                <div class="mt-6 text-right">
                    <p class="text-xl font-semibold">Subtotal: <span id="subtotal">$${subtotal.toFixed(2)}</span></p>
                    <p class="text-sm text-brown-500">Taxes and fees calculated at checkout.</p>
                </div>
                <div class="mt-8 border-t pt-8">
                    <h3 class="text-xl font-bold mb-4 font-serif">Checkout</h3>
                    <form id="checkout-form">
                        <div class="mb-4">
                            <label for="name" class="block text-sm font-medium text-brown-700 mb-1">Full Name</label>
                            <input type="text" id="name" name="name" required class="w-full p-3 border border-cream-300 rounded-md focus:outline-none focus:ring-2 focus:ring-brown-500">
                        </div>
                        <div class="mb-4">
                            <label for="pickup-time" class="block text-sm font-medium text-brown-700 mb-1">Pickup Time</label>
                            <select id="pickup-time" name="pickup-time" required class="w-full p-3 border border-cream-300 rounded-md bg-white focus:outline-none focus:ring-2 focus:ring-brown-500">
                                <option>ASAP (15-20 min)</option>
                                <option>In 30 minutes</option>
                                <option>In 1 hour</option>
                            </select>
                        </div>
                        <button type="submit" class="w-full bg-brown-700 text-white py-3 rounded-full text-lg font-semibold hover:bg-brown-900 transition-transform hover:scale-105 mt-4">Place Order</button>
                    </form>
                </div>
            </div>
        `;

        addEventListeners();
    };

    const updateCart = () => {
        localStorage.setItem('cart', JSON.stringify(cart));
        renderCart();
    };

    const addEventListeners = () => {
        document.querySelectorAll('.quantity-change-btn').forEach(btn => {
            btn.addEventListener('click', e => {
                const itemId = e.target.dataset.itemId;
                const change = e.target.textContent === '+' ? 1 : -1;
                cart.items[itemId].quantity += change;
                if (cart.items[itemId].quantity <= 0) {
                    delete cart.items[itemId];
                }
                if (Object.keys(cart.items).length === 0) cart.shopId = null;
                updateCart();
            });
        });

        document.querySelectorAll('.remove-item-btn').forEach(btn => {
            btn.addEventListener('click', e => {
                const itemId = e.currentTarget.dataset.itemId;
                delete cart.items[itemId];
                if (Object.keys(cart.items).length === 0) cart.shopId = null;
                updateCart();
            });
        });

        const checkoutForm = document.getElementById('checkout-form');
        if (checkoutForm) {
            checkoutForm.addEventListener('submit', e => {
                e.preventDefault();
                const formData = new FormData(checkoutForm);
                const subtotal = Object.values(cart.items).reduce((sum, item) => sum + item.price * item.quantity, 0);
                const orderDetails = {
                    name: formData.get('name'),
                    pickupTime: formData.get('pickup-time'),
                    total: subtotal,
                    orderNumber: Math.floor(100000 + Math.random() * 900000)
                };
                localStorage.setItem('lastOrder', JSON.stringify(orderDetails));
                localStorage.removeItem('cart');
                window.location.href = '/confirmation.html';
            });
        }
    };

    renderCart();
});
